#!/bin/bash\n\necho \"\ud83d\udd10 \uc0ac\uc6a9\uc790 \uc778\uc99d \uc2dc\uc2a4\ud15c \uc124\uc815 \uc911...\"\necho \"\"\n\n# backend \ub514\ub809\ud1a0\ub9ac\ub85c \uc774\ub3d9\ncd backend\n\necho \"\ud83d\udcbe \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2a4\ud0a4\ub9c8 \uc5c5\ub370\uc774\ud2b8 \uc911...\"\nnpx prisma db push\n\necho \"\"\necho \"\u2705 \uc0ac\uc6a9\uc790 \uc778\uc99d \uc2dc\uc2a4\ud15c \uc124\uc815 \uc644\ub8cc!\"\necho \"\"\necho \"\ud83d\udcdd \ucd94\uac00\ub41c \uae30\ub2a5:\"\necho \"\u2705 JWT \ud1a0\ud070 \uae30\ubc18 \uc778\uc99d\"\necho \"\u2705 \ud68c\uc6d0\uac00\uc785/\ub85c\uadf8\uc778/\ub85c\uadf8\uc544\uc6c3\"\necho \"\u2705 \ube44\ubc00\ubc88\ud638 \uc554\ud638\ud654 (bcrypt)\"\necho \"\u2705 \uc0ac\uc6a9\uc790 \ud504\ub85c\ud544 \uad00\ub9ac\"\necho \"\u2705 \uad8c\ud55c\ubcc4 \uc811\uadfc \uc81c\uc5b4\"\necho \"\u2705 \ub9ac\ud504\ub808\uc2dc \ud1a0\ud070 \uc9c0\uc6d0\"\necho \"\"\necho \"\ud83d\ude80 API \uc5d4\ub4dc\ud3ec\uc778\ud2b8:\"\necho \"POST /api/auth/register - \ud68c\uc6d0\uac00\uc785\"\necho \"POST /api/auth/login - \ub85c\uadf8\uc778\"\necho \"POST /api/auth/logout - \ub85c\uadf8\uc544\uc6c3\"\necho \"GET  /api/auth/me - \ub0b4 \uc815\ubcf4 \uc870\ud68c\"\necho \"PUT  /api/auth/profile - \ud504\ub85c\ud544 \uc5c5\ub370\uc774\ud2b8\"\necho \"\"\necho \"\ud83c\udf89 \uc774\uc81c \ub2e4\uc74c \ub2e8\uacc4\ub85c \uc9c4\ud589\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4:\"\necho \"1. \ubc31\uc5d4\ub4dc \uc11c\ubc84 \uc2dc\uc791: npm run dev\"\necho \"2. \ud504\ub860\ud2b8\uc5d4\ub4dc \uc778\uc99d UI \uad6c\ud604\"\necho \"3. API \ud14c\uc2a4\ud2b8\"\n